class ToggleFriend{constructor(e){this.toggleFriendBtn=e,this.toggleFriend()}toggleFriend(){$(this.toggleFriendBtn).click((function(e){e.preventDefault();let t=this;$.ajax({type:"GET",url:$(t).attr("href")}).done((function(e){console.log(e),1==e.data.deleted?(console.log("friendship is removed so deleted(existing friendship) is",e.data.deleted),$(t).html("Add Friend")):(console.log("friendship is added so deleted(existing friendship) is",e.data.deleted),$(t).html("Remove Friend"))})).fail((function(e){console.log("error in completing the request",e.responseText)}))}))}}